import { nanoid } from "nanoid";
export default {
    namespaced: true,
    actions: {
        add(context,value){
            context.commit("ADD",value)
        }
    },
    mutations: {
        ADD: function(state,value){
            console.log("这里是Mutations的value",value);
            const todoObject = {
                id:nanoid(),
                todo:value
            }
            state.todos.push(todoObject)
        }
    },
    state: {
        todos:[
            {   
                id:nanoid(),
                todoTing:{
                    title:'敲代码'
                },
                isFinished:true,
                isTodayTask:true,
                isImportance:true
            },
            {id:nanoid(),todo:'敲代码'},
            {id:nanoid(),todo:'吃饭'},
            {id:nanoid(),todo:'睡觉'},
        ]
    },
    getters: {

    }
}
