import { nanoid } from "nanoid";
export default {
    namespaced: true,
    actions: {
        add(context,value){
            context.commit("ADD",value)
        }
    },
    mutations: {
        ADD: function(state,value){
            console.log("这里是Mutations的value",value);
            const todoObject = {
                id:nanoid(),
                todo:value
            }
            state.todos.push(todoObject)
        }
