import { nanoid } from "nanoid";
import { watch } from "vue";
export default {
    namespaced: true,
    state: {
        // 增、删、改、查
        todos: JSON.localStorage.getItem("todos")||[]
    },

    // 当state中的需要加工可以使用getters并不对数据进行修改
    getters: {

    },


    actions: {
        add(context, value) {
            context.commit("ADD", value)
        }
    },
    mutations: {
        // 这个改一下，不在这里封装大量的逻辑
        ADD: function (state, value) {
            state.todos.push(value)
            window.localStorage.setItem('todos', JSON.stringify(state.todos))
        },
        EDIT: function (state, value) {
            console.log("value", value);
            state.todos.forEach(item => {
                if (value.id == item.id) {
                    item.isEdit = true;
                }
            });
        },
        // 修改内容
        ALTER: function (state, value) {
            state.todos.forEach(item => {
                if (value.id == item.id) {
                    item.isEdit = value.isEdit;
                    item.todoThing.isEdit = value.todoThing.title;
                    console.log("ALTER执行了", item);
                }
            });
        },
        DELETE: function (state, value) {
            const todoArray = state.todos
            for (var i = 0; i < todoArray.length; i++) {
                if (todoArray[i].id == value.id) {
                    todoArray.splice(i, 1)
                }
            }
            console.log(state.todos);
        },
        ALTERCHECKBOX: function (state, value) {
            state.todos.forEach(item => {
                if (item.id == value.id) {
                    item.isFinished = !(item.isFinished)
                    console.log("状态发生改变了", item);
                }
            });
        },
        CHANGEWEIGHT: function (state, value) {
            state.todos.forEach(item => {
                if (item.id == value.id) {
                    item.isImportance = !(item.isImportance)
                    console.log("任务权重发生改变了", item);
                }
            });
        },
        // watchState: function (state, value) {
        //     todos:{
        //         deep:true,
        //         handler(state,value){
        //             console.log("watch执行了");
        //             localStorage.setItem("todos",JSON.stringify(value))
        //         }    
        //     }
        // },

        

    },

}
