import { nanoid } from "nanoid";
export default {
    namespaced: true,
    actions: {
        add(context,value){
            context.commit("ADD",value)
        }
    },
    mutations: {
        ADD: function(state,value){
            
            const todoObject = {
                id:nanoid(),
                todo:'value'
            }
            state.todos.push(todoObject)
        }
    },
    state: {
        todos:[
            {id:nanoid(),todo:'写作业'},
            {id:nanoid(),todo:'敲代码'},
            {id:nanoid(),todo:'吃饭'},
            {id:nanoid(),todo:'睡觉'},
        ]
    },
    getters: {

    }
}
